transparency = 0

# Board State
# 0 = Empty
# 1 = X
# 2 = O
board = [
    [0, 0, 0],
    [0, 0, 0],
    [0, 0, 0],
]

# Record cursor position
# Start cursor at center
cursor_x = 1
cursor_y = 1

# Set inital cursor drawn value
cursor_timer = 0

# Set State
state = "player_turn"


# Draw the Tic Tac Toe gridlines
def draw_grid():
    display.fill(0)
    display.rect(40, 0, 4, 128, Display.Color.White, True) # X Y Width Height
    display.rect(84, 0, 4, 128, Display.Color.White, True) # X Y Width Height
    display.rect(0, 40, 128, 4, Display.Color.White, True) # X Y Width Height
    display.rect(0, 84, 128, 4, Display.Color.White, True) # X Y Width Height


# Draw the X's and O's
def draw_board():
    for y in range(3):
        for x in range(3):
            x_pos = (x*44) if x > 0 else 0
            y_pos = (y*44) if y > 0 else 0

            if board[y][x] == 1:
                display.blit(X, x_pos, y_pos, transparency)
            

            elif board[y][x] == 2:
                display.blit(O, x_pos, y_pos, transparency)


# Draw cursor on the screen
def draw_cursor():
    global cursor_timer, cursor_x, cursor_y

    if cursor_timer < 5:
        display.ellipse(cursor_x * 44 + 20,  cursor_y * 44 + 20, 5, 5, Display.Color.Gray, True)

    cursor_timer = (cursor_timer + 1) % 10

# Create a function to move the cursor
def move_cursor(x:int, y:int) -> None:
    global cursor_x, cursor_y
    print(x, y)
    cursor_x = (cursor_x + x) % 3
    cursor_y = (cursor_y + y) % 3


# Function to update board based on user button press
def update_board(value: int) -> None:
    global cursor_x, cursor_y, state

    if board[cursor_y][cursor_x] == 0:
        board[cursor_y][cursor_x] = value
        state = "computer_turn"


# Set Button handler functions
buttons.on_press(Buttons.Up,    lambda: move_cursor( 0, -1))
buttons.on_press(Buttons.Down,  lambda: move_cursor( 0,  1))
buttons.on_press(Buttons.Left,  lambda: move_cursor(-1,  0))
buttons.on_press(Buttons.Right, lambda: move_cursor( 1,  0))
buttons.on_press(Buttons.A, lambda: update_board(1))


while True:
    if state == "player_turn":
        buttons.scan()
        draw_grid()
        draw_board()
        draw_cursor()
        display.commit()
    elif state == "computer_turn":
        draw_grid()
        draw_board()
        display.commit()

    time.sleep_ms(50)