# Draw the Tic Tac Toe gridlines
def draw_grid():
    display.fill(0)
    display.rect(40, 0, 4, 128, Display.Color.White, True) # X Y Width Height
    display.rect(84, 0, 4, 128, Display.Color.White, True) # X Y Width Height
    display.rect(0, 40, 128, 4, Display.Color.White, True) # X Y Width Height
    display.rect(0, 84, 128, 4, Display.Color.White, True) # X Y Width Height


# Draw the X's and O's
def draw_board():
    for y in range(3):
        for x in range(3):
            x_pos = (x*44) if x > 0 else 0
            y_pos = (y*44) if y > 0 else 0

            if board[y][x] == 1:
                display.blit(X, x_pos, y_pos, transparency)
            

            elif board[y][x] == 2:
                display.blit(O, x_pos, y_pos, transparency)


# Draw cursor on the screen
def draw_cursor():
    global cursor_timer, cursor_x, cursor_y

    if cursor_timer < 5:
        display.ellipse(cursor_x * 44 + 20,  cursor_y * 44 + 20, 5, 5, Display.Color.Gray, True)

    cursor_timer = (cursor_timer + 1) % 10

# Create a function to move the cursor
def move_cursor(x:int, y:int) -> None:
    global cursor_x, cursor_y
    print(x, y)
    cursor_x = (cursor_x + x) % 3
    cursor_y = (cursor_y + y) % 3


# Function to update board based on user button press
def update_board(value: int) -> None:
    global cursor_x, cursor_y, state

    if board[cursor_y][cursor_x] == 0:
        board[cursor_y][cursor_x] = value
        state = "computer_turn"


# Function for handling computer move logic - TODO: Make logic better
def computer_turn() -> None:
    global state
    for y in range(3):
        for x in range(3):
            if board[y][x] == 0:
                board[y][x] = 2
                state = "player_turn"
                return


# Function for detetcing a winner
def has_winner() -> None:
    global board, state, winner

    # Check Rows
    for y in range(3):
        if board[y][0] != 0 and board[y][0] == board[y][1] == board[y][2]:
            state = "winner"
            winner = board[y][0]

    # Check Columns
    for x in range(3):
        if board[0][x] != 0 and board[0][x] == board[1][x] == board[2][x]:
            state = "winner"
            winner = board[0][x]

    # Check \ Diagonal
    if board[1][1] != 0 and board[0][0] == board[1][1] == board[2][2]:
        state = "winner"
        winner = board[1][1]

    # Check / Diagonal
    elif board[1][1] != 0 and board[0][2] == board[1][1] == board[2][0]:
        state = "winner"
        winner = board[1][1]


# Draw Function for winner page
def draw_winner() -> None:

    # Clear screen
    display.fill(0)

    # Draw winning sprite
    if winner == 1:
        display.blit(X, 44, 5, transparency)
    elif winner == 2:
        display.blit(O, 44, 5, transparency)

    display.text("Won", 52, 50, Display.Color.White)

    display.text("Play Again?", 20, 70, Display.Color.White)

    display.text("Yes", 52, 95, Display.Color.White)

    display.text("No",  56, 110, Display.Color.White)


    display.commit()


# Set Button handler functions
buttons.on_press(Buttons.Up,    lambda: move_cursor( 0, -1))
buttons.on_press(Buttons.Down,  lambda: move_cursor( 0,  1))
buttons.on_press(Buttons.Left,  lambda: move_cursor(-1,  0))
buttons.on_press(Buttons.Right, lambda: move_cursor( 1,  0))
buttons.on_press(Buttons.A, lambda: update_board(1))


while True:
    if state == "player_turn":
        buttons.scan()
        draw_grid()
        draw_board()
        draw_cursor()
        display.commit()
        has_winner()
    elif state == "computer_turn":
        computer_turn()
        draw_grid()
        draw_board()
        display.commit()
        has_winner()
    elif state == "winner":
        draw_winner()

    time.sleep_ms(50)