effect_timer = 0           # Timing for sound effects
effect_playing = None      # Sound effect currently playing
state = "player_turn"      # Game State
winner = None              # Store who the winner is
volume = 16384             # Default Volume value of 25%


# Draw the Tic Tac Toe gridlines
def draw_grid():
    display.fill(0)
    display.rect(40, 0, 4, 128, Display.Color.White, True) # X Y Width Height
    display.rect(84, 0, 4, 128, Display.Color.White, True) # X Y Width Height
    display.rect(0, 40, 128, 4, Display.Color.White, True) # X Y Width Height
    display.rect(0, 84, 128, 4, Display.Color.White, True) # X Y Width Height


# Draw the X's and O's
def draw_board():
    for y in range(3):
        for x in range(3):
            x_pos = (x*44) if x > 0 else 0
            y_pos = (y*44) if y > 0 else 0

            if board[y][x] == 1:
                display.blit(X, x_pos, y_pos, transparency)
            

            elif board[y][x] == 2:
                display.blit(O, x_pos, y_pos, transparency)


# Draw cursor on the screen
def draw_cursor():
    global cursor_timer, cursor_x, cursor_y

    if cursor_timer < 5:
        display.ellipse(cursor_x * 44 + 20,  cursor_y * 44 + 20, 5, 5, Display.Color.Gray, True)

    cursor_timer = (cursor_timer + 1) % 10


# Create a function to move the cursor
def move_cursor(x:int, y:int) -> None:
    global cursor_x, cursor_y, state, effect_playing, effect_timer

    # Handle game screen
    if state == "player_turn":
        cursor_x = (cursor_x + x) % 3
        cursor_y = (cursor_y + y) % 3

    # Handle winner screen
    elif state == "winner":
        cursor_y = (cursor_y + y) % 2

    # Handle Menu screen
    elif state == "menu":
        cursor_y = (cursor_y + y) % 2
    
    # Set sound effect
    effect_playing = move_sound
    effect_timer = 0

    # # Verify cursor position
    # print(cursor_x, cursor_y)


# Take an action when a button is pressed
def take_action(value: int) -> None:
    global board, cursor_x, cursor_y, state, effect_playing, effect_timer, music_playing, music_timer

    # Update board on player turn
    if state == "player_turn":
        if board[cursor_y][cursor_x] == 0:
            board[cursor_y][cursor_x] = value
            state = "computer_turn"

        # Set sound effect
        effect_playing = place_sound
        effect_timer = 0

    # Take winner menu action
    elif state == "winner" and value == 1:

        # Set sound effect
        effect_playing = place_sound
        effect_timer = 0

        # Handle User Selecting yes
        if cursor_y == 0:

            # Reset board to blank
            board = [
                [0, 0, 0],
                [0, 0, 0],
                [0, 0, 0],
            ]

            cursor_x, cursor_y = 1, 1  # Reset cursor to center
            state = "player_turn"      # Reset game state
            music_playing = background # Reset background music
            music_timer = 0            # Reset music timer


        # Handle User Selecting no
        elif cursor_y == 1:
            pass
            # TODO: Bring user to menu screen when menu screen is implemented


# Function for handling computer move logic - TODO: Make logic better
def computer_turn() -> None:
    global state
    for y in range(3):
        for x in range(3):
            if board[y][x] == 0:
                board[y][x] = 2
                state = "player_turn"
                return


# Function for detetcing a winner
def has_winner() -> None:
    global board, state, winner, cursor_x, cursor_y, music_playing

    # Check Rows
    for y in range(3):
        if board[y][0] != 0 and board[y][0] == board[y][1] == board[y][2]:
            state = "winner"
            cursor_x, cursor_y = 0, 0
            winner = board[y][0]

    # Check Columns
    for x in range(3):
        if board[0][x] != 0 and board[0][x] == board[1][x] == board[2][x]:
            state = "winner"
            cursor_x, cursor_y = 0, 0
            winner = board[0][x]

    # Check \ Diagonal
    if board[1][1] != 0 and board[0][0] == board[1][1] == board[2][2]:
        state = "winner"
        cursor_x, cursor_y = 0, 0
        winner = board[1][1]

    # Check / Diagonal
    elif board[1][1] != 0 and board[0][2] == board[1][1] == board[2][0]:
        state = "winner"
        cursor_x, cursor_y = 0, 0
        winner = board[1][1]
    
    # Set Winner Screen background music
    if state == "winner":
        music_timer = 0
        if winner == 1:
            music_playing = win_music
        else:
            music_playing = loose_music


# Draw Function for winner page
def draw_winner() -> None:
    global winner, cursor_timer, cursor_y

    # Clear screen
    display.fill(0)

    # Draw winning sprite
    if winner == 1:
        display.blit(X, 44, 5, transparency)
    elif winner == 2:
        display.blit(O, 44, 5, transparency)

    # Write fixed text
    display.text("Won", 52, 50, Display.Color.White)
    display.text("Play Again?", 20, 70, Display.Color.White)

    # Draw cursor on yes
    if cursor_timer < 5 and cursor_y == 0:

        display.rect(50, 93, 28, 11, Display.Color.Gray, True) # X Y Width Height

        display.text("Yes", 52, 95, Display.Color.Black)
        display.text("No",  56, 110, Display.Color.White)

    # Draw cursor on No
    elif cursor_timer < 5 and cursor_y == 1:

        display.rect(54, 108, 20, 11, Display.Color.Gray, True) # X Y Width Height

        display.text("Yes", 52, 95, Display.Color.White)
        display.text("No",  56, 110, Display.Color.Black)

    # Don't draw cursor
    else:
        display.text("Yes", 52, 95, Display.Color.White)
        display.text("No",  56, 110, Display.Color.White)

    # Increment cursor timer
    cursor_timer = (cursor_timer + 1) % 10


# Function to draw the main menu
def draw_menu() -> None:
    global cursor_timer

    # Clear screen
    display.fill(0)

    # Game Name
    display.text("Tic-Tac-Toe", 20, 10, Display.Color.White)

    # Draw Cursor on Single Player
    if cursor_timer < 5 and cursor_y == 0:

        display.rect(10, 78, 108, 11, Display.Color.Gray, True) # X Y Width Height

        display.text("Single Player", 12, 80,  Display.Color.Black)
        display.text("Multiplayer",   20, 100, Display.Color.Gray)

    # Draw cursor on Multiplayer
    elif cursor_timer < 5 and cursor_y == 1:

        display.rect(18, 98, 92, 11, Display.Color.Gray, True) # X Y Width Height

        display.text("Single Player", 12, 80,  Display.Color.White)
        display.text("Multiplayer",   20, 100, Display.Color.White)

    # Don't draw cursor
    else:
        display.text("Single Player", 12, 80,  Display.Color.White)
        display.text("Multiplayer",   20, 100, Display.Color.Gray)

    # Increment cursor timer
    cursor_timer = (cursor_timer + 1) % 10


# Music function
def music() -> None:
    global music_timer, effect_timer, effect_playing

    # Play background music when no effect is running
    if effect_playing is None:
        freq = music_playing[music_timer]
        if freq == 0:
            pwm.duty_u16(0)
        else:
            pwm.duty_u16(volume)
            pwm.freq(freq)
    
    # Otherwise play sound effect
    else:
        freq = effect_playing[effect_timer]
        pwm.freq(freq)
        effect_timer += 1

        # Clear effect playing after effect is done
        if not effect_timer < len(effect_playing):
            effect_playing = None

    music_timer = (music_timer + 1) % len(music_playing)


# Set Button handler functions
buttons.on_press(Buttons.Up,    lambda: move_cursor( 0, -1))
buttons.on_press(Buttons.Down,  lambda: move_cursor( 0,  1))
buttons.on_press(Buttons.Left,  lambda: move_cursor(-1,  0))
buttons.on_press(Buttons.Right, lambda: move_cursor( 1,  0))
buttons.on_press(Buttons.A,     lambda: take_action(1))


# Set piezo buzzer
pwm = machine.PWM(piezo.__pin) # find pin from piezo class
pwm.duty_u16(volume)            # Volume control: valid values [0, 65535], default to 25%


while True:
    if state == "menu":
        buttons.scan()
        draw_menu()
        display.commit()
    if state == "player_turn":
        buttons.scan()
        draw_grid()
        draw_board()
        draw_cursor()
        display.commit()
        has_winner()
    elif state == "computer_turn":
        computer_turn()
        draw_grid()
        draw_board()
        display.commit()
        has_winner()
    elif state == "winner":
        buttons.scan()
        draw_winner()
        display.commit()

    music()
    time.sleep_ms(50)
